import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAddNewTicketMutation } from "./ticketsApiSlice";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSave } from "@fortawesome/free-solid-svg-icons";
import useAuth from "../../hooks/useAuth"
import { useUpdateUserMutation } from "../users/usersApiSlice";

const NewTicketForm = ({ users, students }) => {
  const [updateUser, {
    isLoading: isLoadingUser,
    isError: isErrorUser,
    isSuccess: isSuccessUser,
    error: errorUser
  }] = useUpdateUserMutation()

  const [addNewTicket, { isLoading, isSuccess, isError, error }] =
    useAddNewTicketMutation();
  const navigate = useNavigate();
  const { username, isAdmin, isManager, isStudent } = useAuth();
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");
  const [userId, setUserId] = useState(users[0].id);
  const [studentName, setStudentName] = useState("");

  let user;
  if (!isStudent){
    user = users.filter((foundUser)=>foundUser.username === username)[0];
  } else {
    const FilteredEmployees = users.filter((user)=>!user.roles.includes("Manager") && !user.roles.includes("Admin"))
    user = FilteredEmployees[0]
    for (var i = 1 ; i < FilteredEmployees.length; i++){
      if (user.ticketNo > FilteredEmployees[i].ticketsNo){
        console.log(user)
        console.log(FilteredEmployees[i])
        user = FilteredEmployees[i]
      }
    }
    //setUserId(user.id)
    //I need user assigned tickets counter!
  }


  useEffect(() => {
    if (isSuccess && isSuccessUser) {
      setTitle("");
      setText("");
      setUserId("");
      navigate("/dash/tickets");
    }
  }, [isSuccess, isSuccessUser, navigate]);

  const onTitleChanged = (e) => setTitle(e.target.value);
  const onTextChanged = (e) => setText(e.target.value);
  const onUserIdChanged = (e) => setUserId(e.target.value);

  const [filteredSuggestions, setFilteredSuggestions] = useState([]);
  const [showSuggestions, setShowsuggestions] = useState(false);
  const [accepted, setAccepted] = useState(false);
  const [studentId, setStudentId] = useState("");
  const handleStudentNameChange = (e) => {
    setFilteredSuggestions(
      students.filter((suggestion) =>
        suggestion.username.toLowerCase().startsWith(e.target.value.toLowerCase())
      )
    );
    setAccepted(false);
    setStudentName(e.target.value);  
    setShowsuggestions(true);
  };

  const handleOnStudentClicked = (student) => {
    setStudentName(student.username);
    setStudentId(student.id);
    setAccepted(true);
    setShowsuggestions(false);
  };

  const canSave =
    [title, text, userId, studentName, accepted].every(Boolean) && !isLoading && !isLoadingUser;
  const onSaveTicketClicked = async (e) => {
    e.preventDefault();
    if (canSave) {
      const assignedUSer = users.filter((foundUser)=>foundUser.id === userId)[0];
      const ticketNoNew = assignedUSer.ticketsNo + 1
      await addNewTicket({ user: userId, title, text , student: studentId});
      await updateUser({...assignedUSer, ticketsNo: ticketNoNew})
    }
  };
  let options = (
    <option key={user.id} value={user.id}>
        {" "}
        {user.username}
      </option>
  )
  if(isAdmin || isManager){
    options = users.map((user0) => {
      return (
        <option key={user0.id} value={user0.id}>
          {" "}
          {user0.username}
        </option>
      );
    });
  }

  const errClass = isError || isErrorUser ? "errmsg" : "offscreen";
  const validTitleClass = !title ? "form__input--incomplete" : "";
  const validTextClass = !text ? "form__input--incomplete" : "";

  const content = (
    <>
      <p className={errClass}>{error?.data?.message} {errorUser?.data?.message}</p>

      <form className="form" onSubmit={onSaveTicketClicked}>
        <div className="form__title-row">
          <h2>New Ticket</h2>
          <div className="form__action-buttons">
            <button className="icon-button" title="Save" disabled={!canSave}>
              <FontAwesomeIcon icon={faSave} />
            </button>
          </div>
        </div>
        <label className="form__label" htmlFor="title">
          Title:
        </label>
        <input
          className={`form__input ${validTitleClass}`}
          id="title"
          name="title"
          type="text"
          autoComplete="off"
          value={title}
          onChange={onTitleChanged}
        />

        <label className="form__label" htmlFor="text">
          Text:
        </label>
        <textarea
          className={`form__input form__input--text ${validTextClass}`}
          id="text"
          name="text"
          value={text}
          onChange={onTextChanged}
        />
        <div className="user_student_form_container">
          <label
            className="form__label form__checkbox-container"
            htmlFor="username"
          >
            ASSIGNED TO:
          </label>
          <select
            id="username"
            name="username"
            className="form__select"
            value={userId}
            onChange={onUserIdChanged}
          >
            {options}
          </select>
          <div className = "student_search_bar">
            <label className="form__input student_input" htmlFor="Student">
              Student:
            </label>
            <input 
              type = "text"
              id="username"
              name="username"
              className="form__select"
              value={studentName}
              onChange={handleStudentNameChange}
              autoComplete="off"
            ></input>
            {showSuggestions && studentName !== "" && (
                <ul className = "students_ul"> 
                    {filteredSuggestions.map((suggestion) => (
                        <li className="student_li" key={suggestion.id} onClick={()=>{handleOnStudentClicked(suggestion)}}>
                            {suggestion.username}
                        </li>
                    ))}
                </ul>
            )}
          </div>
        </div>
      </form>
    </>
  );

  return content;
};

export default NewTicketForm;
