import { useGetTicketsQuery } from "./ticketsApiSlice";
import Ticket from "./Ticket";
import useAuth from "../../hooks/useAuth";

const TicketsList = () => {
    const {
        data: tickets,
        isLoading,
        isSuccess,
        isError,
        error
    } = useGetTicketsQuery("ticketsList", {
        pollingInterval: 15000,
        refetchOnFocus: true,
        refetchOnMountOrArgChange: true
    })

    let content

    if (isLoading) content = <p>Loading...</p>

    if (isError) {
        content = <p className="errmsg">{error?.data?.message}</p>
    }
    const {username, isAdmin, isManager, isStudent} = useAuth();

    if (isSuccess) {
        const { ids, entities } = tickets;
        let filteredids;
        if (isAdmin || isManager){
            filteredids = [...ids];
        } else if (isStudent){
            filteredids = ids.filter(ticketId => entities[ticketId].studentusername === username)
        }
        else {
            filteredids = ids.filter(ticketId => entities[ticketId].username === username)
        }
        const tableContent = filteredids?.length
            ? filteredids.map(ticketId => <Ticket key={ticketId} ticketId={ticketId} />)
            : null

        content = (
            <div className="table-container">
            <table className="table table--notes">
                <thead className="table__thead">
                    <tr>
                        <th scope="col" className="table__th note__status">Username</th>
                        <th scope="col" className="table__th note__created">Created</th>
                        <th scope="col" className="table__th note__updated">Updated</th>
                        <th scope="col" className="table__th note__title">Title</th>
                        <th scope="col" className="table__th note__username">IT Member</th>
                        <th scope="col" className="table__th note__username">Student</th>
                        <th scope="col" className="table__th note__edit">Edit</th>
                    </tr>
                </thead>
                <tbody>
                    {tableContent}
                </tbody>
            </table>
            </div>
        )
    }

    return content
}
export default TicketsList
