import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAddNewTicketMutation } from "./ticketsApiSlice";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSave } from "@fortawesome/free-solid-svg-icons";

const NewTicketForm = ({ users, students }) => {
  const [addNewTicket, { isLoading, isSuccess, isError, error }] =
    useAddNewTicketMutation();

  const navigate = useNavigate();

  const [title, setTitle] = useState("");
  const [text, setText] = useState("");
  const [userId, setUserId] = useState(users[0].id);
  const [studentName, setStudentName] = useState("");

  useEffect(() => {
    if (isSuccess) {
      setTitle("");
      setText("");
      setUserId("");
      navigate("/dash/tickets");
    }
  }, [isSuccess, navigate]);

  const onTitleChanged = (e) => setTitle(e.target.value);
  const onTextChanged = (e) => setText(e.target.value);
  const onUserIdChanged = (e) => setUserId(e.target.value);
  const onStudentNameChanged = (e) => setStudentName(e.target.value);
  const [filteredSuggestions, setFilteredSuggestions] = useState([]);
  const [showSuggestions, setShowsuggestions] = useState(false);
  const [accepted, setAccepted] = useState(false);
  const handleStudentNameChange = (e) => {
    setStudentName(e.target.value);
    setFilteredSuggestions(
      students.filter((suggestion) =>
        suggestion.username.toLowerCase().startsWith(studentName.lowerCase())
      )
    );
  };

  const canSave =
    [title, text, userId, studentName].every(Boolean) && !isLoading;
  const onSaveTicketClicked = async (e) => {
    e.preventDefault();
    if (canSave) {
      await addNewTicket({ user: userId, title, text });
    }
  };

  const options = users.map((user) => {
    return (
      <option key={user.id} value={user.id}>
        {" "}
        {user.username}
      </option>
    );
  });

  const errClass = isError ? "errmsg" : "offscreen";
  const validTitleClass = !title ? "form__input--incomplete" : "";
  const validTextClass = !text ? "form__input--incomplete" : "";

  const content = (
    <>
      <p className={errClass}>{error?.data?.message}</p>

      <form className="form" onSubmit={onSaveTicketClicked}>
        <div className="form__title-row">
          <h2>New Ticket</h2>
          <div className="form__action-buttons">
            <button className="icon-button" title="Save" disabled={!canSave}>
              <FontAwesomeIcon icon={faSave} />
            </button>
          </div>
        </div>
        <label className="form__label" htmlFor="title">
          Title:
        </label>
        <input
          className={`form__input ${validTitleClass}`}
          id="title"
          name="title"
          type="text"
          autoComplete="off"
          value={title}
          onChange={onTitleChanged}
        />

        <label className="form__label" htmlFor="text">
          Text:
        </label>
        <textarea
          className={`form__input form__input--text ${validTextClass}`}
          id="text"
          name="text"
          value={text}
          onChange={onTextChanged}
        />
        <div className="user_student_form_container">
          <label
            className="form__label form__checkbox-container"
            htmlFor="username"
          >
            ASSIGNED TO:
          </label>
          <select
            id="username"
            name="username"
            className="form__select"
            value={userId}
            onChange={onUserIdChanged}
          >
            {options}
          </select>

          <label className="form__input student_input" htmlFor="Student">
            Student:
          </label>
          <input
            id="username"
            name="username"
            className="form__select"
            value={studentName}
            onChange={onStudentNameChanged}
          ></input>
        </div>
      </form>
    </>
  );

  return content;
};

export default NewTicketForm;
