import NewTicketForm from './NewTicketForm'
import { useGetUsersQuery } from '../users/usersApiSlice'
import PulseLoader from 'react-spinners/PulseLoader'
import useTitle from '../../hooks/useTitle'
import { useGetStudentsQuery } from "../students/studentsApiSlice"
import useAuth from "../../hooks/useAuth"

const NewTicket = () => {
    useTitle('techTickets: New Ticket')

    const { users } = useGetUsersQuery("usersList", {
        selectFromResult: ({ data }) => ({
            users: data?.ids.map(id => data?.entities[id])
        }),
    })

    const { students } = useGetStudentsQuery("studentsList", {
        selectFromResult: ({ data }) => ({
            students: data?.ids.map(id => data?.entities[id])
        }),
    })

    if (!users?.length || !students?.length) return <PulseLoader color={"#FFF"} />

    const content = <NewTicketForm users={users} students= {students}/>

    return content
}
export default NewTicket
